mapper.select:
SELECT COMMENT_ID AS commentId , POST_ID AS postId , USER_ID AS userId , PARENT_COMMENT_ID AS parentCommentId , COMMENT AS comment , DATE_FORMAT(CREATE_AT,'%Y/%m/%d') AS createAt FROM COMMENTS WHERE POST_ID = #{postId}
AND PARENT_COMMENT_ID IS NULL
AND PARENT_COMMENT_ID = #{parentCommentId}
ORDER BY COMMENT_ID

mapper.insert:
INSERT INTO COMMENTS (
	COMMENT_ID
 	, POST_ID
	, USER_ID
	, PARENT_COMMENT_ID
	, COMMENT
) VALUES (
	(SELECT NVL(MAX(COMMENT_ID) + 1, 1) FROM COMMENTS AS SEQ)
	, #{postId}
	, #{userId}
	, #{parentCommentId}
	, #{comment}
)
